// import store from '../store'
import { connect } from 'react-redux'

@connect(
    state => ({ num: state }), //状态映射
    {
        add: () => ({ type: 'add' }),
        minus: () => ({ type: 'minus' }),
        asyncAdd: () => dispatch => {
            setTimeout(() =>{
                dispatch({type, add})
            }, 1000)
        }
    }
)
class ReduxTest extends Component {
    render() {
        return(
            <div>
                {/* <p>{store.getState()}</p>
