import React, { Component } from 'react'
import store from './store'

export default class ReduxTest extends Component {

    render() {
        return(
            <div>
                <p>{store.getState()}</p>
                <div>
                    <button onClick={() => store.dispatch({type: 'add'})}>+</button>
                    <button onClick={() => store.dispatch({type: 'minus'})}>-</button>
                </div>
            </div>
        )
    }
}
